import React, { useState, useEffect } from 'react';
import propTypes from 'prop-types';
import AppContext from './AppContext';

function AppProvider({ children }) {

  useEffect(() => {
    const getList = async () => {
      const response = await getDrinkByIngredientList();
      setDrinkIngredientList(response.drinks);
    };
    getList();
  }, []);

  const data = {
    email,
    setEmail,
    password,
    setPassword,
    optionRadio,
    setOptionRadio,
    inputSearch,
    setInputSearch,
    recipesReturn,
    setRecipesReturn,
    cocktailsReturn,
    setCocktailsReturn,
    categoriesFood,
    setCategoriesFood,
    categoriesDrink,
    setCategoriesDrink,
    redirectDetailsDrinks,
    setRedirectDetailsDrinks,
    redirectDetailsFoods,
    setRedirectDetailsFoods,
    randomID,
    setRandomID,
    foodIngredientList,
    setFoodIngredientList,
    drinkIngredientList,
    setDrinkIngredientList,
    toggleFilterFood,
    setToggleFilterFood,
    toggleFilterDrink,
    setToggleFilterDrink,
    selectedCategoryFood,
    setSelectedCategoryFood,
    selectedCategoryDrink,
    setSelectedCategoryDrink,
    areaList,
    setAreaList,

  };

  return (
    <AppContext.Provider value={ data }>
      {children}
    </AppContext.Provider>
  );
}

AppProvider.propTypes = {
  children: propTypes.props,
}.isRequired;

export default AppProvider;
